package week.day1;

import java.util.Iterator;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("printing the odd number from 1 to 20");

		for (int i = 1; i < 20; i++) {
			
			if (i%2==1) {
				
				System.out.println(i);
				
				
			}
			
		}
		
	}

}
